mapper.select:
SELECT COUNT(*)
FROM user
WHERE LENGTH(user_id) > 0
AND user_nm = #{name}
AND user_birthday = #{birthDay}
AND user_sex = #{sex}

        SELECT COUNT(user_id) count
		FROM user
		WHERE user_id = #{id}

SELECT user_id id, user_pw pw, user_nm name, user_email email, user_phone phoneNumber, user_birthday birthDay, user_sex sex
FROM user
WHERE user_id = #{id}
AND user_pw = #{pw}
